import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faStar as fullStar,
  faStarHalfAlt,
} from "@fortawesome/free-solid-svg-icons";
import { faStar as emptyStar } from "@fortawesome/free-regular-svg-icons";

const RatingElement = (props) => {
  //   const { rating } = props;

  function s() {
    let e = [];
    let i = 1;
    if (rating <= 4 && rating % 1 !== 1) {
      for (i; i < parseFloat(rating); i++) {
        e.push(<FontAwesomeIcon key={i} icon={fullStar} />);
      }
      e.push(<FontAwesomeIcon key={i} icon={faStarHalfAlt} />);
    }

    if (e.length !== 5) {
      e.push(<FontAwesomeIcon key={i + 1} icon={emptyStar} />);
    }

    return e;
  }

  return <h1> {s()} </h1>;
};

export default RatingElement;
