import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faStar as fullStar,
  faStarHalfAlt,
} from "@fortawesome/free-solid-svg-icons";
import { faStar as emptyStar } from "@fortawesome/free-regular-svg-icons";

const RatingElement = (props) => {
  const { rating } = props;

  let rateStars = parseFloat(rating);

  console.log("tjsi", rateStars % 1, rateStars);

  function s() {
    let e = [];
    let i = 1;
    if (rateStars < 5 && rateStars % 1 == 0.5) {
      console.log("im");
      for (i; i < parseFloat(rateStars); i++) {
        console.log("1st loop");
        e.push(<FontAwesomeIcon key={i} icon={fullStar} />);
      }
      e.push(<FontAwesomeIcon key={i} icon={faStarHalfAlt} />);
    } else {
      for (i; i < parseFloat(rateStars); i++) {
        console.log("2st loop");
        e.push(<FontAwesomeIcon key={i} icon={fullStar} />);
      }
    }
    if (e.length !== 5) {
      for (i; i < 5; i++) {
        e.push(<FontAwesomeIcon key={i + 1} icon={emptyStar} />);
      }
    }

    return e;
  }

  return (
    <h1>
      {s()} {rating}
    </h1>
  );
};

export default RatingElement;
